class TrainingTask extends React.Component {
  constructor(props) {
    super(props);

    let trialTotal = 9;//update later

    let trialSgmMu = Array(trialTotal).fill().map(() => Array(3).fill(0));
    let trialRT = Array(trialTotal).fill().map(() => Array(3).fill(0));

    this.state = {
      // userID: userID,
