      if (i < nr_train_a_trial / 2) {
        check_al1[i] = getRand(restricted);
        check_al2[i] = 100 - corr_values[i];
      } else {
        check_al1[i] = 100 - corr_values[i];
        check_al2[i] = getRand(restricted);
      }
