      var block_tmp = this.state.blockNum + 1;
      var trialBlockNum = this.state.trialBlockNum + 1;
      this.setState({
        trialNum: trialNum_tmp,
        blockNum: block_tmp,
        trialBlockNum: trialBlockNum,
        feedback: false,
        timePassed: false,
        timePassed2: false,
      });
    }
  }

