import React, { useState }  from "react";
import { withRouter } from "react-router-dom";
import { DATABASE_URL } from "./config";
import styles from "./style/taskStyle.module.css";
import Slider from "./slider";

import Cockpit from "./img/CockpitBlank.jpg";
/////////////////////////////////////////////////////////////////////////////////

var trialTotal = 9;

/////////////////////////////////////////////////////////////////////////////////
// Plan the outcome memory test here

/////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////
// REACT COMPONENT START
class TrainingTask extends React.Component {
  constructor(props) {
    super(props);

    const userID = this.props.location.state.userID;
    const date = this.props.location.state.date;
    const startTime = this.props.location.state.startTime;
    var currentDate = new Date(); // maybe change to local
    var timeString = currentDate.toTimeString();

    /////////////////////////////////////////////////////////////////////////////////
    // SET COMPONENT STATES
    this.state = {
      userID: userID,
      date: date,
      startTime: startTime,
      sectionTime: timeString,
      taskSessionTry: 1,
      taskSession: "TrainingTask",
      instructScreenText: 1,

      trialNum: 1,
      trialTotal: trialTotal,
      trialRT: 0,
      trialTime: 0,
      trialKeypress: 0,
      trialCorLog: [],
      trialScore: 0,
      trialCor: null,

      instructScreen: true,
      testScreen: false,

      debug: false, //if true, skip this section

      mu: 0,
      std: 1
    };

    this.handleInstructLocal = this.handleInstructLocal.bind(this);
    this.testStart = this.testStart.bind(this);

    /* prevents page from going down when space bar is hit .*/
    window.addEventListener("keydown", function (e) {
      if (e.keyCode === 32 && e.target === document.body) {
        e.preventDefault();
      }
    });
  }
  /////////////////////////////////////////////////////////////////////////////////
  // END COMPONENT STATE

  // This handles instruction screen within the component USING KEYBOARD
  handleInstructLocal(key_pressed) {
    var curText = this.state.instructScreenText;
    var whichButton = key_pressed;

    if (whichButton === 4 && curText > 1) {
      this.setState({ instructScreenText: curText - 1 });
    } else if (whichButton === 5 && curText < 6) {
      this.setState({ instructScreenText: curText + 1 });
    } else if (curText === 6 && whichButton === 10) {
      //startmissionOne
      setTimeout(
        function () {
          this.missionOne();
        }.bind(this),
        0
      );
    } else if (curText === 7 && whichButton === 10) {
      //restart
      this.setState({
        instructScreenText: 1,
      });
    } else if (curText === 8 && whichButton === 10) {
      //go to pathtask
      setTimeout(
        function () {
          this.nextMission();
        }.bind(this),
        0
      );
    }
  }

  // handle key key_pressed
  _handleInstructKey = (event) => {
    var key_pressed;

    switch (event.keyCode) {
      case 37:
      //    this is left arrow
      key_pressed = 4;
      this.handleInstructLocal(key_pressed);
      break;
      case 39:
      //    this is right arrow
      key_pressed = 5;
      this.handleInstructLocal(key_pressed);
      break;
      case 32:
      //    this is SPACEBAR
      key_pressed = 10;
      this.handleInstructLocal(key_pressed);
      break;
      default:
    }
  };

  _handleTestKey = (event) => {
    var pressed;
    var time_pressed;

    switch (event.keyCode) {
      case 49:
      pressed = 1;
      time_pressed = Math.round(performance.now());
      this.testCheck(pressed, time_pressed);
      break;
      case 50:
      pressed = 2;
      time_pressed = Math.round(performance.now());
      this.testCheck(pressed, time_pressed);
      break;
      case 51:
      pressed = 3;
      time_pressed = Math.round(performance.now());
      this.testCheck(pressed, time_pressed);
      break;
      //this is keycode for numpad
      case 97:
      pressed = 1;
      time_pressed = Math.round(performance.now());
      this.testCheck(pressed, time_pressed);
      break;
      case 98:
      pressed = 2;
      time_pressed = Math.round(performance.now());
      this.testCheck(pressed, time_pressed);
      break;
      case 99:
      pressed = 3;
      time_pressed = Math.round(performance.now());
      this.testCheck(pressed, time_pressed);
      break;
      default:
    }
  };

  /////////////////////////////////////////////////////////////////////////////////
  // END COMPONENT PROPS

  missionOne() {
    var trialTime = Math.round(performance.now());
    this.setState({
      instructScreen: false,
      testScreen: true,
      trialNum: 1,
      trialTime: trialTime,
      trialRT: 0,
      trialKeypress: 0,
      trialCorLog: [],
      trialScore: 0,
    });
  }

  testStart(trialNum) {
    // which picture
    //
    // var whichOutcomePic = this.state.outcomeIndxLog[trialNum - 1];
    //
    // //how many pics
    // var numOutcomePic = this.state.outcomeMultLog[trialNum - 1];
    //
    // //value of the answer
    // var outcomeVal1 = this.state.outcomeValLog[trialNum - 1];
    // var outcomeVal2 = this.state.outcomeNotValLog1[trialNum - 1];
    // var outcomeVal3 = this.state.outcomeNotValLog2[trialNum - 1];
    //
    // // position of the answer
    // var outcomePos1 = this.state.outcomeAnsLog[trialNum - 1];
    // var outcomePos2 = this.state.outcomeNotAnsLog1[trialNum - 1];
    // // var outcomePos3 = this.state.outcomeNotAnsLog2[trialNum - 1];
    //
    // var ansOne;
    // var ansTwo;
    // var ansThree;
    //
    // // console.log(this.state.outcomeValLog);
    // // console.log(this.state.outcomeNotValLog1);
    // // console.log(this.state.outcomeNotValLog1);
    // // console.log(this.state.outcomeAnsLog);
    // // console.log(this.state.outcomeNotAnsLog1);
    // // console.log(this.state.outcomeNotAnsLog2);
    //
    // if (outcomePos1 === 1) {
    //   ansOne = outcomeVal1;
    // } else {
    //   if (outcomePos2 === 1) {
    //     ansOne = outcomeVal2;
    //   } else {
    //     ansOne = outcomeVal3;
    //   }
    // }
    //
    // if (outcomePos1 === 2) {
    //   ansTwo = outcomeVal1;
    // } else {
    //   if (outcomePos2 === 2) {
    //     ansTwo = outcomeVal2;
    //   } else {
    //     ansTwo = outcomeVal3;
    //   }
    // }
    //
    // if (outcomePos1 === 3) {
    //   ansThree = outcomeVal1;
    // } else {
    //   if (outcomePos2 === 3) {
    //     ansThree = outcomeVal2;
    //   } else {
    //     ansThree = outcomeVal3;
    //   }
    // }
    //
    // // console.log(ansOne);
    // // console.log(ansTwo);
    // // console.log(ansThree);

    let text = (
      <div className={styles.questions}>
      How large is the alien population?
      <br />
      <br />
      <br />
      </div>
    );
    // return <div>{text}</div>;
    return (<div>
      <div>{text}</div>
      <Slider onSpacebarHit={(result) => window.alert(JSON.stringify(result))} />
    </div>)
  }


  condSave() {
    var userID = this.state.userID;
    var currentDate = new Date(); // maybe change to local
    var sectionTime = currentDate.toTimeString();
    var trialTime = Math.round(performance.now());

    let saveString = {
      userID: this.state.userID,
      date: this.state.date,
